import React, { Component } from "react";

class ScrollBox extends Component {
    render() {
        const style = {
            border: '5px soild black',
            height: '300px',
            width: '300px',
            overflow: 'auto',
            position: 'relative',
            background: 'green' 
        };

        const innerStyle = {
            width: '100px',
            height: '650px',
            background: 'linear-gradient(white, black)'
        }

        return(
            <>
                <h1>ScrollBox</h1>
                <div
                    style={style}
                    ref={(ref) => {this.box=ref}}>
                        <div style={innerStyle}/>
                </div>
            </>
        )
    }
}

export default ScrollBox;
